import React, { useState } from "react";

const Userinput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredAge, setEnteredAge] = useState("");
  const [isValid, setisValid] = useState(true);

  const nameInputChangeHandler = (event) => {
    if (event.target.value.trim().length > 0) {
      setisValid(true);
    }
    setEnteredName(event.target.value);
  };

  const ageInputChangeHandler = (event) => {
    if (event.target.value > 0) {
      setisValid(true);
    }
    setEnteredAge(event.target.value);
  };

  const formSubmitHandler = (event) => {
    event.preventDefault();
    if (enteredName.trim().length === 0 || enteredAge.value < 1) {
      setisValid(false);
      return;
    }
    props.onAddPerson(enteredName);
    setisValid(true);
  };

  return (
    <form onSubmit={formSubmitHandler}>
      <div className="Formcontrol" invalid={!isValid}>
        <label>age</label>
        <input type="text" onChange={nameInputChangeHandler} />

        <label>age</label>
        <input type="number" onChange={ageInputChangeHandler} />
      </div>

      <button type="submit"></button>
    </form>
  );
};

export default Userinput;
