import React, { useState } from "react";
import Card from "../UI/Card";

const Userinput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredAge, setEnteredAge] = useState("");
  const [isValid, setisValid] = useState(true);

  const nameInputChangeHandler = (event) => {
    if (event.target.value.trim().length > 0) {
      setisValid(true);
    }
    setEnteredName(event.target.value);
  };

  const ageInputChangeHandler = (event) => {
    if (event.target.value > 0) {
      setisValid(true);
    }
    setEnteredAge(event.target.value);
  };

  const formSubmitHandler = (event) => {
    event.preventDefault();
    if (enteredName.trim().length === 0 || enteredAge.value < 1) {
      setisValid(false);
      return;
    }
    props.onAddUser(enteredName, enteredAge);
    setisValid(true);

  };



  return (
    <Card>
      <form onSubmit={formSubmitHandler} className="addUserForm">
        {/* <div className="Formcontrol" invalid={!isValid}> */}
        <label htmlFor="username">Username</label>
        <input id="username" type="text" onChange={nameInputChangeHandler} />

        <label htmlFor="age">age</label>
        <input id="age" type="number" onChange={ageInputChangeHandler} />
        {/* </div> */}

        <button type="submit">Add new User</button>
      </form>
    </Card>
  );
};

export default Userinput;
